// import logo from './logo.svg';
import Gallery from './Gallery.js';
import Profile from './Profile.js';
import Avatar from './Avatar.js';
import GridX from './GridX.js';
import Time from './time/Time.js';
import './App.css';
import './styles.css';

const props = {
  avatar:{
    size:100,
    person:{
      name: 'Katsuko Saruhashi',
      imageId: 'YfeOqp2'
    }
  },
  show:{
    clock: false,
    avatar: false,
    gallery: false,
    grid: false,
    profile: false
  }

}

const person = {
  name: 'Gregorio Y. Zara',
  theme: {
    backgroundColor: 'black',
    color: 'pink'
  }
};

const Card = ({ children }) => {
  return (
    <div className="card">
      {children}
    </div>
  );
}










let App = ( ) => {
  return (
    <div>
      <Card>
        { props.show.clock && <Time />}
        </Card>
      <Card>
      { props.show.avatar &&  <div className="avatar">
        <Avatar {...props.avatar } />
      </div>  }





      </Card>

      <Profile />
      <br/>
      <Avatar
        size={100}
        person={{ 
          name: 'Katsuko Saruhashi', 
          imageId: 'YfeOqp2'
        }}
      />
      <Avatar
        size={80}
        person={{
          name: 'Aklilu Lemma', 
          imageId: 'OKS67lh'
        }}
      />
      <Avatar
        size={50}
        person={{ 
          name: 'Lin Lanying',
          imageId: '1bX5QH6'
        }}
      />
      <br/>


      <Gallery/>
      <GridX/>
      <div style={person.theme}>
      <h1>{person.name}'s Todos</h1>
      <img
        className="avatar"
        src="https://i.imgur.com/7vQD0fPs.jpg"
        alt="Gregorio Y. Zara"
      />
      <ul>
        <li>Improve the videophone</li>
        <li>Prepare aeronautics lectures</li>
        <li>Work on the alcohol-fuelled engine</li>
      </ul>



    </div>


    </div>


  );
}


export default App;
