// import logo from './logo.svg';
import Gallery from './components/Gallery.js';
import Profile from './components/Profile.js';
import Avatar from './components/Avatar.js';
import GridX from './components/GridX.js';
import Time from './components/time/Time.js';
import List from './components/ListT.js';
import ListP from './components/people/people.js';
import Tea from './components/Tea.js';
import MyFancy from './components/MyFancy.js';
import './App.css';
import './styles.css';

const props = {
  avatar: {
    size: 100,
    person: {
      name: 'Katsuko Saruhashi',
      imageId: 'YfeOqp2'
    }
  },
  show: {
    clock: false,
    avatar: false,
    gallery: false,
    grid: false,
    profile: false,
    list: false,
    people:false,
    tea: true,
    fancy:true
  }

}

const person = {
  name: 'Gregorio Y. Zara',
  theme: {
    backgroundColor: 'black',
    color: 'pink'
  }
};

const Card = ({ children }) => {
  return (
    <div className="card">
      {children}
    </div>
  );
}


let App = () => {
  return (
    <div>
       {props.show.clock && <Card><Time /></Card>}
       {props.show.profile && <Card><Profile /></Card>}
       {props.show.grid && <Card><GridX/></Card>}
       {props.show.person &&
          <Card>
            <div style={person.theme}>
              <h1>{person.name}'s Todos</h1>
              <img
                className="avatar"
                src="https://i.imgur.com/7vQD0fPs.jpg"
                alt="Gregorio Y. Zara"
              />
              <ul>
                <li>Improve the videophone</li>
                <li>Prepare aeronautics lectures</li>
                <li>Work on the alcohol-fuelled engine</li>
              </ul>
            </div>
          </Card>
        }


      {props.show.avatar &&
          <Card>
            <div className="avatar"><Avatar {...props.avatar} /></div>
          </Card>

        }

      {props.show.list &&  <Card><List /></Card>}
      {props.show.gallery && <Card><Gallery /></Card>}
      {props.show.people && <Card><ListP /></Card>}
      {props.show.tea && <Card><Tea /></Card>}
      {props.show.fancy && <Card><MyFancy /></Card>}

    </div>


  );
}


export default App;
